import { useState } from "react";

import { useEffect } from "react";

const ScrollTop = () => {
  const [ScrollTop, setScrollTop] = useState(false);

  useEffect(() => {
    window.addEventListener("scroll", () => {
      if (window.scrollY >0) {
        setScrollTop(true);
      } else {
        setScrollTop(false);
      }
    });
  }, []);

  const ScrollUP = () => {
    window.scrollTo(0,0);
  };

  return (
    <div>
      {ScrollTop && (
        <button onClick={ScrollUP} className="topbtn">
          â‡‘
        </button>
      )}
    </div>
  );
};
export default ScrollTop;
